import React, {useState, useEffect } from 'react'
import axios from 'axios'

export const UserContext = React.createContext()

//Here we want to create a header to hold our token to get controversies by user
//we create our own instance of axios
const userAxios = axios.create()
//then we add the Authorization and bearer stuff to it, in the request
userAxios.interceptors.request.use(config => {
  const token = localStorage.getItem("token")
config.headers.Authorization = `Bearer ${token}`
return config
})

export default function UserProvider(props) {

//console.log('user provider context props:', props)

const initState = { 
  user:JSON.parse(localStorage.getItem("user")) || {}, 
  token: localStorage.getItem("token") || "",
  controversies: [],
  errMsg: ""
}
const [ userState, setUserState ] = useState(initState);

function signup(credentials) {
  axios.post("/auth/signup", credentials)
    .then(res => {
      const { user, token } = res.data
      localStorage.setItem("user", JSON.stringify(user))
      localStorage.setItem("token", token)
      setUserState (prevUserState => ({
        ...prevUserState, 
        user, 
        token
      }))
    })
    .catch(err => handleAuthErr(err.response.data.errMsg))
}

function login(credentials) {
  axios.post("/auth/login", credentials)
  .then(res => {
    const { user, token } = res.data
      localStorage.setItem("user", JSON.stringify(user))
      localStorage.setItem("token", token)
      getUserReviews()
      setUserState (prevUserState => ({
        ...prevUserState, 
        user, 
        token
      }))
  })
  .catch(err => handleAuthErr(err.response.data.errMsg))
}

function logout() {
  localStorage.removeItem("token")
  localStorage.removeItem("user")
  setUserState({
    user: {},
    token: "",
    controversies: []
  })
}

function handleAuthErr(errMsg) {
  setUserState(prevState => ({
    ...prevState,
    errMsg
  }))
}

function resetAuthErr() {
  setUserState(prevState => ({
    ...prevState,
    errMsg: ""
  }))
}

// function getUserReviews() {
//   //the userAxios has the token in it now
//   userAxios.get("/api/controversies/user")
//     .then(res => {
//       setUserState(prevState => ({
//         ...prevState,
//         controversies : res.data
//       }),
//       console.log("in context controversies: ",userState.controversies))
//     })
//     .catch(err => console.log(err.response.data.errMsg))
// }

const getNStoreUserReviews = useEffect(()=>{
  userAxios.get("/api/controversies/user")
    .then(res => {
      setUserState(prevState => ({
        ...prevState,
        controversies : res.data
      }),
      console.log("in context controversies: ", userState.controversies))
    })
    .catch(err => console.log(err.response.data.errMsg))
}, [])

function addReview(newReview) {
  //the userAxios has the token in it now
  userAxios.post("/api/controversies", newReview)
  .then(res => {
    setUserState(prevState => ({
      ...prevState,
      controversies: [ ...prevState.controversies, newReview]
    }))
  })
  .catch(err => console.log(err.response.data.errMsg))
}

  return  (
    <UserContext.Provider
      value={{
        ...userState,
        signup,
        login,
        logout,
        //getUserReviews, 
        getNStoreUserReviews,
        addReview,
        resetAuthErr
      }} >
      {props.children}
    </UserContext.Provider>
  )
}
