import React, {useState, useEffect } from 'react'
import axios from 'axios'

export const UserContext = React.createContext()

export default function UserProvider(props) {
//console.log("props in user provider: ", props)

const initState = { 
  user:JSON.parse(localStorage.getItem("user")) || {}, 
  token: localStorage.getItem("token") || "",
  controversiesByCurrentUser: [],
  errMsg: ""
}
const [ userState, setUserState ] = useState(initState);

function signup(credentials) {
  axios.post("/auth/signup", credentials)
    .then(res => {
      const { user, token } = res.data
      localStorage.setItem("user", JSON.stringify(user))
      localStorage.setItem("token", token)
      setUserState (prevUserState => ({
        ...prevUserState, 
        user, 
        token
      }))
    })
    .catch(err => handleAuthErr(err.response.data.errMsg))
}

function login(credentials, getUserReviews) {
  axios.post("/auth/login", credentials)
  .then(res => {
    const { user, token } = res.data
      localStorage.setItem("user", JSON.stringify(user))
      localStorage.setItem("token", token);
      //getUserReviews()
      console.log("user state in the middle of login" ,userState)
      setUserState (prevUserState => ({
        ...prevUserState, 
        user, 
        token
      }))
      console.log("user state at the end of login" ,userState)
  })
  .catch(err => console.log(err)//handleAuthErr(err.response.data.errMsg)
   )
}

function logout() {
  localStorage.removeItem("token")
  localStorage.removeItem("user")
  setUserState(prevState =>({
    ...prevState, 
    user: {},
    token: "",
    controversiesByCurrentUser: []
  }))
  console.log("user state at the end of logOUT" ,userState)
}

function handleAuthErr(errMsg) {
  setUserState(prevState => ({
    ...prevState,
    errMsg
  }))
}

function resetAuthErr() {
  setUserState(prevState => ({
    ...prevState,
    errMsg: ""
  }))
}

  return  (
    <UserContext.Provider
      value={{
        ...userState,
        signup,
        login,
        logout,
        //addReview,
        resetAuthErr
      }} >
      {props.children}
    </UserContext.Provider>
  )
}
